import styles from '../styles/scss/profile.module.scss';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';

const Profile = () => {
	const months = [
		'January',
		'February',
		'March',
		'April',
		'May',
		'June',
		'July',
		'August',
		'September',
		'October',
		'November',
		'December'
	];
	const [ currentDate, setCurrentDate ] = useState('');

	const profileImage = useSelector((state) => state.profileImage);
	console.log('profile image in profile component: ' + profileImage);

	useEffect(() => {
		const currentDateInfo = new Date();
		const month = months[currentDateInfo.getMonth()];
		const day = currentDateInfo.getDate();
		const year = currentDateInfo.getFullYear();
		setCurrentDate(month + ' ' + day + ', ' + year);
	}, []);

	return (
		<div className={styles.profileContainer}>
			<img className={styles.profileImage} src={profileImage} alt={'Sarthak Kamboj'} />
			<h2>
				{'Hey '}Sarthak Kamboj{'!'}
			</h2>
			<h5>{currentDate}</h5>
		</div>
	);
};

export default Profile;
