import styles from '../styles/scss/sign-up.module.scss';
import Link from 'next/link';
import classnames from 'classnames';
import { useState, useRef } from 'react';
const Joi = require('@hapi/joi');

const SignUp = () => {
	const [ email, setEmail ] = useState('');
	const [ username, setUsername ] = useState('');
	const [ password, setPassword ] = useState('');
	const [ emailInputEmpty, setEmailInputEmpty ] = useState(true);
	const [ passwordInputEmpty, setPasswordInputEmpty ] = useState(true);
	const [ usernameEmpty, setUsernameEmpty ] = useState(true);
	const [ viewPassword, setViewPassword ] = useState(false);
	const [ error, setError ] = useState(null);

	// const usernameRef = useRef();
	// const emailRef = useRef();
	// const passwordRef = useRef();

	const signUpSchema = Joi.object({
		username: Joi.string().required(),
		email: Joi.string().required().email({ minDomainSegments: 2, tlds: { allow: [ 'com', 'net' ] } }),
		password: Joi.string().required().min(5)
	});

	const updateEmail = (input) => {
		setEmail(input);
		if (input === '') setEmailInputEmpty(true);
		else setEmailInputEmpty(false);
	};

	const updatePassword = (input) => {
		setPassword(input);
		if (input === '') setPasswordInputEmpty(true);
		else setPasswordInputEmpty(false);
	};

	const updateUsername = (input) => {
		setUsername(input);
		if (input === '') setUsernameEmpty(true);
		else setUsernameEmpty(false);
	};

	const checkIfEncodeUriValid = (input) => {
		return encodeURI(input) === input;
	};

	const submitSignUpInfo = () => {
		// const username = usernameRef.current.value;
		// const email =

		const usernameUriEcoded = checkIfEncodeUriValid(username);
		const emailUriEcoded = checkIfEncodeUriValid(email);
		const passwordUriEcoded = checkIfEncodeUriValid(password);
		const signUpValidation = signUpSchema.validate({ username, email, password });
		if (signUpValidation.error) {
			console.log(signUpValidation.error.split('"')[1]);
			return setError(signUpValidation.error);
		}
		if (!usernameUriEcoded || !emailUriEcoded || !passwordUriEcoded) {
			return setError('Login information needs to be in only letters and numbers!');
		}
		setError(null);
	};

	const toggleViewPassword = () => {
		setViewPassword(!viewPassword);
	};

	return (
		<main className={styles.signUpContainer}>
			<section className={styles.textSection}>
				<div className={styles.logoImage}>
					<img src="./images/logo.jpg" alt="Workflow" />
				</div>
				<div className={styles.signUpSection}>
					<h1>
						Sign Up to <br /> Workflow
					</h1>
					<div className={styles.googleSignIn}>
						<img src="/images/google_logo.png" />
						<h4>Sign Up with Google</h4>
					</div>
					<div className={styles.emailSignIn}>
						<p>Or sign-up with email</p>
						<form>
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.usernameLabel]: true,
									[styles.usernameLabelHover]: !usernameEmpty
								})}
							>
								Username
							</h4>
							<input
								value={username}
								onChange={(e) => updateUsername(e.target.value)}
								type="text"
								required
							/>
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.emailLabel]: true,
									[styles.emailLabelHover]: !emailInputEmpty
								})}
							>
								Email
							</h4>
							<input value={email} onChange={(e) => updateEmail(e.target.value)} type="email" required />
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.passwordLabel]: true,
									[styles.passwordLabelHover]: !passwordInputEmpty
								})}
							>
								Password
							</h4>
							<input
								value={password}
								onChange={(e) => updatePassword(e.target.value)}
								type={viewPassword ? 'text' : 'password'}
								required
							/>
							<img
								src="/images/eye.png"
								onClick={() => toggleViewPassword()}
								className={styles.eyeIcon}
								alt="Toggle Password"
							/>
							<div onClick={() => submitSignUpInfo()} className={styles.submitEmailForm}>
								Create Your Account
							</div>
						</form>
						<p className={styles.legal}>
							By signing up, I agree to the <span className={styles.underline}>Privacy Policy</span> and{' '}
							<span className={styles.underline}>Terms of Service</span>
						</p>
					</div>
					<h3 className={styles.switchToLogin}>
						Already have an account?{'  '}
						<Link href="/login">
							<span className={styles.loginBtn}>Log in</span>
						</Link>
					</h3>
				</div>
				<div className={styles.copyright}>&copy; 2020. All Rights Reserved. Workflow.</div>
			</section>
			<section className={styles.slideshowSection} />
		</main>
	);
};

export default SignUp;
