import { Doughnut } from 'react-chartjs-2';

const DonutChart = ({ data, chartTitle }) => {
	const breakdownData = (canvas) => {
		canvas.style.width = '50%';
		canvas.style.height = '45%';
	};

	const chartOptions = {
		legend: {
			position: 'right',
			labels: {
				// fontSize: 25
			}
		},
		tooltips: {
			// bodyFontSize: 30,
			padding: 5
		},
		title: {
			text: chartTitle
		}
	};

	return (
		<Doughnut
			data={breakdownData}
			responsive={true}
			// options={chartOptions}
			className={styles.chart}
			options={chartOptions}
		/>
	);
};

export default DonutChart;
