import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import styles from '../styles/scss/file-upload.module.scss';
import { useRef } from 'react';
import { useSelector } from 'react-redux';

const AddFile = ({ fileType, id }) => {
	// e.preventDefault();
	const imageInput = useRef(null);

	const jwtToken = useSelector((state) => state.jwtToken);
	const submitFile = async () => {
		const res = await fetch(`http://localhost:8000/upload-file/${id}/${fileType}`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json',
				Authorization: 'Bearer ' + jwtToken
			},
			body: imageInput.files[0]
		});
		const resJson = await res.json();
		console.log(resJson);
	};

	return (
		<div className={styles.fileUploadContainer}>
			<FontAwesomeIcon icon={'plus-circle'} size={70} className={styles.fileUpload} />
			{/* <form
				enctype="multipart/form-data"
				method="POST"
				action={`http://localhost:8000/upload-file/${id}/${fileType}`}
			> */}
			<input
				ref={imageInput}
				type="file"
				accept={`${fileType}/*`}
				name="file"
				onChange={() => {
					submitFile();
				}}
			/>
			{/* </form> */}
		</div>
	);
};

export default AddFile;
