import styles from '../styles/scss/workflow-description.module.scss';
import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import classnames from 'classnames';

const WorkflowDescription = ({ initialDescription, id }) => {
	const [ description, setDescription ] = useState(null);
	const [ descriptionChanged, setDescriptionChanged ] = useState(false);

	useEffect(() => {
		setDescription(initialDescription);
	});

	const updateDesc = (e) => {
		setDescription(e.target.value);
		setDescriptionChanged(e.target.value !== initialDescription);
		console.log(description !== initialDescription);
	};

	return (
		<div className={styles.descriptionContainer}>
			<h2 className={styles.descriptionTitle}>Description</h2>
			<br />
			<textarea
				name="description"
				className={styles.description}
				defaultValue={description}
				onChange={(e) => updateDesc(e)}
			/>
			<FontAwesomeIcon
				icon={'arrow-right'}
				className={classnames({ [styles.updateIcon]: true, [styles.descriptionChanged]: descriptionChanged })}
			/>
		</div>
	);
};

export default WorkflowDescription;
