import styles from '../../styles/scss/workflows.module.scss';
import classnames from 'classnames';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProfileImage } from '../../components/actions/profileImageActions';
import { setProfileName } from '../../components/actions/profileNameActions';
import DashboardMenu from '../../components/DashboardMenu';
import WorkflowItem from '../../components/WorkflowItem';
import CreateWorkflowModal from '../../components/CreateWorkflowModal';

const Dashboard = () => {
	// const currentProfileImage = useSelector((state) => state.profileImage);

	const [ workflows, setWorkflows ] = useState(null);
	const [ displayModal, setDisplayModal ] = useState(false);
	const jwtToken = useSelector((state) => state.jwtToken);

	useEffect(() => {
		// const tempWorkflows = [
		// 	{ title: 'Workflow 1', keywords: [ 'blog', 'comedy' ] },
		// 	{ title: 'Workflow 1', keywords: [ 'blog', 'comedy' ] },
		// 	{ title: 'Workflow 1', keywords: [ 'blog', 'comedy' ] }
		// ];
		const workflowsResponse = fetch('http://localhost:8000/get-workflows', {
			headers: {
				Authorization: 'Bearer ' + jwtToken
			}
		});
		setWorkflows(tempWorkflows);
	}, []);

	const toggleModalVisibility = () => {
		setDisplayModal(!displayModal);
	};

	// if (currentProfileImage === '') {
	// 	const dispatch = useDispatch();
	// 	dispatch(setProfileImage(props.profileImage));
	// 	dispatch(setProfileName(props.profileName));
	// }

	return (
		<main className={styles.workflowsContainer}>
			<div className={styles.workflowsMenuContainer}>
				<DashboardMenu />
			</div>
			<div className={styles.workflowsBody}>
				{!workflows ? (
					<p className={styles.noWorkflows}>You don't have any workflows. Let's get one started!</p>
				) : (
					<div className={styles.workflowList}>
						{workflows.map((workflow) => (
							<WorkflowItem workflow={workflow} key={workflow.title} />
							// <div className={styles.workflow} key={index}>
							// <div className={styles.workflowTitle}>{workflow.title}</div>
							// 	<ul className={styles.workflowKeywords}>
							// 		{workflow.keywords.map(keyword=>(
							// 			<li key={keyword}>
							// 				{capitalize(keyword)}
							// 			</li>
							// 		))}
							// 	</ul>
							// </div>
						))}
					</div>
				)}
				<div className={styles.addWorkflowBtn} onClick={() => toggleModalVisibility()}>
					Add Workflow
				</div>
				{/* <CreateWorkflowModal displayModal={displayModal} toggleModalVisibility={toggleModalVisibility} /> */}
				{/* <Link href="" */}
			</div>
		</main>
	);
};

export async function getStaticProps() {
	// const profileImage = 'https://i.imgur.com/0byOhwQ.jpg';
	// const profileName = 'Sarthak Kamboj';
	// return {
	// 	props: {
	// 		profileImage,
	// 		profileName
	// 	}
	// };
}

export default Dashboard;
