import { Line } from 'react-chartjs-2';

const LineChart = ({ labels, breakdown, chartTitle }) => {
	const breakdownData = (canvas) => {
		return {
			labels: labels,
			datasets: [
				{
					// label: 'File Breakdown',
					data: breakdown,
					backgroundColor: [ '#DC1C13', '#F1959B', '#EA4C46', '#F07470' ],
					borderWidth: 2,
					hoverBorderColor: 'black',
					borderColor: 'white'
				}
			]
		};
	};

	const chartOptions = {
		// legend: {
		// 	// position: 'right',
		// 	labels: {
		// 		// fontSize: 25
		// 	}
		// },
		legend: {
			display: false
		},
		tooltips: {
			// bodyFontSize: 30,
			padding: 5
		},
		title: {
			text: chartTitle,
			display: true,
			fontSize: 20
		},
		scales: {
			yAxes: [
				{
					ticks: {
						beginAtZero: true,
						maxTicksLimit: 5
					}
				}
			],
			xAxes: [
				{
					// display: false //this will remove all the x-axis grid lines
				}
			]
		}
	};

	return <Line data={breakdownData} responsive={true} options={chartOptions} />;
};

export default LineChart;
