	// 	canvas.style.height = '45%';
	// };

	const chartOptions = {
		legend: {
			position: 'right',
			labels: {
				// fontSize: 25
			}
		},
		tooltips: {
			// bodyFontSize: 30,
			padding: 5
		},
		title: {
			text: chartTitle
		}
	};

	return (
		<Doughnut
			data={data}
			responsive={true}
			// options={chartOptions}
			options={chartOptions}
		/>
	);
};

export default DonutChart;
