import styles from '../styles/scss/sign-in.module.scss';
import classnames from 'classnames';
import { useState } from 'react';
import Link from 'next/link';

const Login = () => {
	const [ email, setEmail ] = useState('');
	const [ password, setPassword ] = useState('');

	const [ emailInputEmpty, setEmailInputEmpty ] = useState(true);
	const [ passwordInputEmpty, setPasswordInputEmpty ] = useState(true);

	const updateEmail = (input) => {
		setEmail(input);
		if (input === '') setEmailInputEmpty(true);
		else setEmailInputEmpty(false);
	};

	const updatePassword = (input) => {
		setPassword(input);
		if (input === '') setPasswordInputEmpty(true);
		else setPasswordInputEmpty(false);
	};

	return (
		<main className={styles.loginContainer}>
			<div className={styles.images}>
				<img src="/images/cartoon-youtube.png" alt="Youtube Workflow" />
			</div>
			<div className={styles.loginTxt}>
				<img className={styles.logoImg} src="/images/logo.jpg" />
				<h1>Welcome Back!</h1>
				<h5>
					To keep putting out more of your amazing videos, please login in with your email address and
					password below.
				</h5>
				<div className={styles.signInSection}>
					<div className={styles.googleSignIn}>
						<img src="/images/google_logo.png" />
						<h4>Sign In with Google</h4>
					</div>
					<div className={styles.emailSignIn}>
						<p>Or sign-in with email</p>
						<form>
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.emailLabel]: true,
									[styles.emailLabelHover]: !emailInputEmpty
								})}
							>
								Email
							</h4>
							<input value={email} onChange={(e) => updateEmail(e.target.value)} type="email" required />
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.passwordLabel]: true,
									[styles.passwordLabelHover]: !passwordInputEmpty
								})}
							>
								Password
							</h4>
							<input
								value={password}
								onChange={(e) => updatePassword(e.target.value)}
								type="password"
								required
							/>
							<div className={styles.submitEmailForm}>Sign In</div>
						</form>
					</div>
					<h3 className={styles.switchToSignUp}>
						Don't have an account?{'  '}
						<Link href="/sign-up">
							<span className={styles.signUpBtn}>Sign Up</span>
						</Link>
					</h3>
				</div>
			</div>
		</main>
	);
};

export default Login;
