import { Doughnut } from 'react-chartjs-2';

const DonutChart = ({ data }) => {
	const chartOptions = {
		legend: {
			position: 'right',
			labels: {
				// fontSize: 25
			}
		},
		tooltips: {
			// bodyFontSize: 30,
			padding: 5
		}
	};

	return (
		<Doughnut
			data={data}
			responsive={true}
			// options={chartOptions}
			className={styles.chart}
			options={Object.freeze(
				{
					title: 'Finished vs. Unfinished',
					fontSize: 25
				},
				chartOptions
			)}
		/>
	);
};

export default DonutChart;
