let jwt = '';

const setJwtInJS = (newJwt) => {
	if (localStorage.getItem('jwt')) {
		jwt = localStorage.getItem('jwt');
		return;
	}
	jwt = newJwt;
};

setJwtInJS();

export { setJwtInJS, jwt };
