import styles from '../styles/scss/dashboard-menu.module.scss';
import Profile from './Profile';
import DashboardMenuItem from './DashboardMenuItem';
import { setProfileImage } from '../../components/actions/profileImageActions';
import { setProfileName } from '../../components/actions/profileNameActions';
import { useRouter } from 'next/router';
import { useSelector, useDispatch } from 'react-redux';

const DashboardMenu = () => {
	const currentProfileImage = useSelector((state) => state.profileImage);

	if (!currentProfileImage) {
		const dispatch = useDispatch();
		dispatch(setProfileImage(props.profileImage));
		dispatch(setProfileName(props.profileName));
	}

	const router = useRouter();
	const curRoute = router.route;
	const currentPage = curRoute.split('/')[2];

	return (
		<div className={styles.dashboardMenuContainer}>
			<div className={styles.divProfile}>
				<Profile />
			</div>
			<div className={styles.dashboardMenuItems}>
				<DashboardMenuItem
					menuTitle={'Home'}
					menuItemFontAwesomeRef={'home'}
					menuLinkReference={'/dashboard/home'}
					menuItemActive={currentPage == 'home'}
				/>
				<DashboardMenuItem
					menuTitle={'Workflows'}
					menuItemFontAwesomeRef={'sitemap'}
					menuLinkReference={'/dashboard/workflows'}
					menuItemActive={currentPage == 'workflows'}
				/>
				<DashboardMenuItem
					menuTitle={'Shared with me'}
					menuItemFontAwesomeRef={'share-alt'}
					menuLinkReference={'/dashboard/shared-with-me'}
					menuItemActive={currentPage == 'shared-with-me'}
				/>
				<DashboardMenuItem
					menuTitle={'Starred'}
					menuItemFontAwesomeRef={'star'}
					menuLinkReference={'/dashboard/starred'}
					menuItemActive={currentPage == 'starred'}
				/>
				<DashboardMenuItem
					menuTitle={'Statistics'}
					menuItemFontAwesomeRef={'chart-pie'}
					menuLinkReference={'/dashboard/statistics'}
					menuItemActive={currentPage == 'statistics'}
				/>
				<DashboardMenuItem
					menuTitle={'Profile'}
					menuItemFontAwesomeRef={'user-alt'}
					menuLinkReference={'/dashboard/profile'}
					menuItemActive={currentPage == 'profile'}
				/>
			</div>
			{/* <div className={styles.summaryStats}> */}
			{/* <p>
					<span className={styles.numWorkflowsInProgress}>5</span>
					<br />
					<span className={styles.workflowsInProgressTitle}>Workflows in Progress</span>
				</p> */}
			{/* <img src="/images/workflow_images.png" alt="Workflows" /> */}
			{/* </div> */}
		</div>
	);
};

export async function getStaticProps() {
	const profileImage = 'https://i.imgur.com/0byOhwQ.jpg';
	const profileName = 'Sarthak Kamboj';
	return {
		props: {
			profileImage,
			profileName
		}
	};
}

export default DashboardMenu;
