import '../styles/global.css';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import allReducers from '../components/reducers/allReducers';
import { library } from '@fortawesome/fontawesome-svg-core';
import { fab } from '@fortawesome/free-brands-svg-icons';
import { faHome, faShareAlt, faStar, faChartPie, faUserAlt, faSitemap } from '@fortawesome/free-solid-svg-icons';

const icons = [ faHome, faShareAlt, faStar, faChartPie, faUserAlt, faSitemap ];

library.add(fab, ...icons);

const App = ({ Component, pageProps }) => {
	let store;

	if (window) {
		store = createStore(allReducers, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());
	} else {
		store = createStore(allReducers);
	}

	return (
		<Provider store={store}>
			<Component {...pageProps} />
		</Provider>
	);
};

export default App;
