import styles from '../styles/scss/profile.module.scss';
import { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Profile = () => {
	const months = [
		'January',
		'February',
		'March',
		'April',
		'May',
		'June',
		'July',
		'August',
		'September',
		'October',
		'November',
		'December'
	];
	const [ currentDate, setCurrentDate ] = useState('');

	const profileImage = useSelector((state) => state.profileImage);
	const profileName = useSelector((state) => state.profileName);
	const profileFirstName = profileName.split(' ')[0];

	useEffect(() => {
		const currentDateInfo = new Date();
		const month = months[currentDateInfo.getMonth()];
		const day = currentDateInfo.getDate();
		const year = currentDateInfo.getFullYear();
		setCurrentDate(month + ' ' + day + ', ' + year);
	}, []);

	return (
		<div className={styles.profileContainer}>
			<div className={styles.imageInputSection}>
				<img className={styles.profileImage} src={profileImage} alt={profileName} />
				<FontAwesomeIcon className={styles.editIcon} icon={'user-edit'} />
				<input type="file" className={styles.profileImageInput} />
			</div>
			<h2>Hey {profileFirstName}!</h2>
			<h5>{currentDate}</h5>
		</div>
	);
};

export default Profile;
