import styles from '../styles/scss/workflow-description.module.scss';
import { useState, useEffect, useRef } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import classnames from 'classnames';

const WorkflowDescription = ({ oldDescription, id }) => {
	const [ description, setDescription ] = useState(null);
	const [ descriptionChanged, setDescriptionChanged ] = useState(false);
	const jwtToken = useSelector((state) => state.jwtToken);
	const descTextArea = useRef(null);

	useEffect(() => {
		console.log(oldDescription);
		setDescription(oldDescription);
	});

	const updateDescToBackend = async () => {
		console.log('description: ' + descTextArea.current.value);
		const newDescJson = JSON.stringify({
			description
		});

		console.log(
			`http://127.0.0.1:8000/set-workflow-description/${id}` ===
				'http://127.0.0.1:8000/set-workflow-description/8'
		);
		const res = await fetch(`http://127.0.0.1:8000/set-workflow-description/${id}`, {
			method: 'PUT',
			headers: {
				'Content-Type': 'application/json',
				Authorization: 'Bearer ' + jwtToken
			},
			body: newDescJson
		});
		const resJson = await res.json();
		console.log(resJson);
		// oldDescription = description;
		// setDescriptionChanged(false);
	};

	const updateDesc = (e) => {
		console.log(e.target.value);
		setDescription(e.target.value);
		setDescriptionChanged(e.target.value !== oldDescription);
		console.log(description !== oldDescription);
	};

	return (
		<div className={styles.descriptionContainer}>
			<h2 className={styles.descriptionTitle}>Description</h2>
			<textarea
				ref={descTextArea}
				name="description"
				className={styles.description}
				defaultValue={description}
				onChange={(e) => updateDesc(e)}
			/>
			<FontAwesomeIcon
				icon={'arrow-right'}
				className={classnames({ [styles.updateIcon]: true, [styles.descriptionChanged]: descriptionChanged })}
				onClick={() => updateDescToBackend()}
			/>
		</div>
	);
};

export default WorkflowDescription;
