import styles from '../styles/scss/workflow-description.module.scss';
import { useState, useEffect } from 'react';

const WorkflowDescription = ({ id }) => {
	const [ description, setDescription ] = useState();
	// const updateDescription = (e) => {
	// 	setDescription(e.target.value);
	// };
	useEffect(
		() => {
			fetch(`http://127.0.0.1:8000/get-workflow-description/${id}`)
				.then((desc) => setDescription(desc))
				.catch((err) => console.log(err));
		},
		[ description ]
	);
	return (
		<React.Fragment>
			<h2 className={styles.descriptionTitle}>Description</h2>
			<br />
			<textarea name="description" className={styles.description}>
				{initialDescription}
			</textarea>
		</React.Fragment>
	);
};

export default WorkflowDescription;
