import styles from '../styles/scss/workflow-item.module.scss';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useState, useRef } from 'react';
import classnames from 'classnames';

const WorkflowItem = ({ workflow, id }) => {
	// const [ workflowContainerGripped, setWorkflowContainerGripped ] = useState(false);

	// const workflowContainer = useRef();

	console.log(id);

	const capitalize = (txt) => {
		return txt.slice(0, 1).toUpperCase() + txt.slice(1);
	};

	const maxDescriptionLengthBeforeCutoff = 30;

	const profileImage = useSelector((state) => state.profileImage);

	// const scaleElement = (e) => {
	// 	e.target.style.transform = 'scale(1.02)';
	// 	e.target.style.opacity = '1';
	// };

	// const dragStart = (e) => {
	// 	const target = e.target;

	// 	// e.dataTransfer

	// 	setTimeout(() => {
	// 		target.style.display = 'none';
	// 	}, 0);
	// };

	// const dragOver = (e) => {
	// 	e.stopPropagation();
	// };

	// const mountDraggable = (e) => {
	// 	setWorkflowContainerGripped(true);
	// };

	// const unmountDraggle = (e) => {
	// 	setWorkflowContainerGripped(false);
	// };

	return (
		<div className={classnames({ [styles.workflowContainer]: true })}>
			<h2 className={styles.workflowTitle}>{workflow.title}</h2>
			<h4>
				{workflow.description.length > maxDescriptionLengthBeforeCutoff ? (
					workflow.description.slice(0, maxDescriptionLengthBeforeCutoff) + '...'
				) : (
					workflow.description
				)}
			</h4>
			<div>
				<div className={styles.workflowUsers}>
					<img src={profileImage} />
				</div>
				<div className={styles.workflowExtraInfo}>
					<h3 className={styles.workflowCreated}>{workflow.created_at}</h3>
					<div className={styles.workflowStatusIcons}>
						<FontAwesomeIcon className={classnames({ [styles.starred]: workflow.starred })} icon="star" />
						<FontAwesomeIcon
							className={classnames({ [styles.finished]: workflow.finished })}
							icon="check"
						/>
						{/* <FontAwesomeIcon
						icon="grip-vertical"
						className={styles.grip}
						// onMouseDown={(e) => mountDraggable(e)}
						// onMouseUp={(e) => unmountDraggle(e)}
					/> */}
					</div>
				</div>
			</div>
		</div>
	);
};

export default WorkflowItem;
