import styles from '../../styles/scss/workflows.module.scss';
import classnames from 'classnames';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProfileImage } from '../../components/actions/profileImageActions';
import { setProfileName } from '../../components/actions/profileNameActions';
import { setJwt } from '../../components/actions/jwtAction';
import DashboardMenu from '../../components/DashboardMenu';
import WorkflowItem from '../../components/WorkflowItem';
import Router from 'next/router';
import CreateWorkflowModal from '../../components/CreateWorkflowModal';

const Dashboard = () => {
	// const currentProfileImage = useSelector((state) => state.profileImage);
	console.log('dashboard');
	const [ workflows, setWorkflows ] = useState(null);
	const [ displayModal, setDisplayModal ] = useState(false);
	const jwtToken = useSelector((state) => state.jwtToken);
	// console.log('jwtToken: ' + (jwtToken === ''));
	const dispatch = useDispatch();
	const state = useSelector((state) => state);
	console.log(state);

	// if (jwtToken === '') {
	// 	return <p>Loading</p>;
	// }

	const getNewJwtToken = async () => {
		const refreshToken = localStorage.getItem('refresh-token');
		const data = {
			refresh: refreshToken
		};
		const dataStringified = JSON.stringify(data);
		const newAccessTokenRes = await fetch('http://127.0.0.1:8000/api/refresh', {
			method: 'POST',
			body: dataStringified,
			headers: {
				'Content-Type': 'application/json'
			}
		});
		const newAccessTokenJson = await newAccessTokenRes.json();
		if (newAccessTokenJson.detail === 'Token is invalid or expired') {
			localStorage.setItem('refresh-token', '');
			dispatch(setJwt(''));
			return Router.push('/');
		}
		const newJwtToken = newAccessTokenJson.access;
		dispatch(setJwt(newJwtToken));
		const workflowsResponse = await fetch('http://localhost:8000/get-workflows', {
			headers: {
				Authorization: 'Bearer ' + newJwtToken
			}
		});
		console.log(workflowsResponse);
		const workflowsJson = await workflowsResponse.json();
		if (workflowsJson.code) {
			console.log('jwt was not correct');
			await getNewJwtToken();
			return;
		}
		setWorkflows(workflowsJson['workflows']);
		console.log(workflowsJson);
		await getWorkflows();
		// window.location = window.location.href;
	};

	const getWorkflows = async () => {
		// console.log('in workflows.js');]
		// console.log(jwtToken);
		const jwtBearerString = 'Bearer ' + jwtToken;
		// console.log(jwtBearerString);
		const workflowsResponse = await fetch('http://localhost:8000/get-workflows', {
			headers: {
				Authorization: jwtBearerString
			}
		});
		const workflowsJson = await workflowsResponse.json();
		// console.log(workflowsJson);
		if (workflowsJson.code) {
			// console.log('jwt was not correct');
			await getNewJwtToken();
			return;
		}
		setWorkflows(workflowsJson['workflows']);
		// console.log(workflowsJson);
	};

	useEffect(() => {
		console.log('use effect');
		getWorkflows();
	}, []);

	const toggleModalVisibility = () => {
		setDisplayModal(!displayModal);
	};

	return (
		<main className={styles.workflowsContainer}>
			<div className={styles.workflowsMenuContainer}>
				<DashboardMenu />
			</div>
			<div className={styles.workflowsBody}>
				{!workflows ? (
					<p className={styles.noWorkflows}>You don't have any workflows. Let's get one started!</p>
				) : (
					<div className={styles.workflowList}>
						{workflows.map((workflow) => <WorkflowItem workflow={workflow} key={workflow.id} />)}
						{/* {workflows.map((workflow) => (
							<WorkflowItem workflow={workflow} key={workflow.title} />
							// <div className={styles.workflow} key={index}>
							// <div className={styles.workflowTitle}>{workflow.title}</div>
							// 	<ul className={styles.workflowKeywords}>
							// 		{workflow.keywords.map(keyword=>(
							// 			<li key={keyword}>
							// 				{capitalize(keyword)}
							// 			</li>
							// 		))}
							// 	</ul>
							// </div>
						))} */}
					</div>
				)}
				<div className={styles.addWorkflowBtn} onClick={() => toggleModalVisibility()}>
					Add Workflow
				</div>
				{/* <CreateWorkflowModal displayModal={displayModal} toggleModalVisibility={toggleModalVisibility} /> */}
				{/* <Link href="" */}
			</div>
		</main>
	);
};

// export async function getStaticProps() {
// 	// const profileImage = 'https://i.imgur.com/0byOhwQ.jpg';
// 	// const profileName = 'Sarthak Kamboj';
// 	// return {
// 	// 	props: {
// 	// 		profileImage,
// 	// 		profileName
// 	// 	}
// 	// };
// }

export default Dashboard;
