import styles from '../styles/scss/profile.module.scss';
import { useEffect, useState, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { setProfileImage } from '../components/actions/profileImageActions';
import Router from 'next/router';

const Profile = () => {
	const months = [
		'January',
		'February',
		'March',
		'April',
		'May',
		'June',
		'July',
		'August',
		'September',
		'October',
		'November',
		'December'
	];
	const [ currentDate, setCurrentDate ] = useState('');

	const profileImageFormRef = useRef();
	const jwtToken = useSelector((state) => state.jwtToken);

	const profileImage = useSelector((state) => state.profileImage);
	console.log(profileImage);
	const profileName = useSelector((state) => state.profileName);
	const profileFirstName = profileName.split(' ')[0];

	const dispatch = useDispatch();

	const updateProfileImage = async () => {
		const profileForm = new FormData(profileImageFormRef.current);
		const res = await fetch('http://localhost:8000/upload-profile-image', {
			method: 'POST',
			headers: {
				Authorization: 'Bearer ' + jwtToken
			},
			body: profileForm
		});
		if (res.status !== 200) {
			return;
		}
		const resJson = await res.json();
		dispatch(setProfileImage(resJson['msg']));
		Router.push(Router.pathname);
	};

	useEffect(() => {
		const currentDateInfo = new Date();
		const month = months[currentDateInfo.getMonth()];
		const day = currentDateInfo.getDate();
		const year = currentDateInfo.getFullYear();
		setCurrentDate(month + ' ' + day + ', ' + year);
	}, []);

	return (
		<div className={styles.profileContainer}>
			<div className={styles.imageInputSection}>
				<img className={styles.profileImage} src={profileImage} alt={profileName} />
				<FontAwesomeIcon className={styles.editIcon} icon={'user-edit'} />
				<form encType="multipart/form-data" ref={profileImageFormRef}>
					<input
						type="file"
						onChange={updateProfileImage}
						accept={'image/*'}
						className={styles.profileImageInput}
						name="image"
					/>
				</form>
			</div>
			<h2>Hey {profileFirstName}!</h2>
			<h5>{currentDate}</h5>
		</div>
	);
};

export default Profile;
