import { useSelector } from 'react-redux';
// import { jwt } from '../../components/JWT';
import Router from 'next/router';
import React, { useState, useEffect } from 'react';
import DashboardMenu from '../../components/DashboardMenu';
import styles from '../../styles/scss/worklow_editor.module.scss';

// const dispatch = useDispatch();

const Workflow = () => {
	// console.log(Router.pathname);
	const jwtToken = useSelector((state) => state.jwtToken);

	const [ audioUrls, setAudioUrls ] = useState([]);
	const [ imageUrls, setImageUrls ] = useState([]);
	const [ videoUrls, setVideoUrls ] = useState([]);
	const [ videoEditingUrls, setVideoEditingUrls ] = useState([]);
	const id = Router.query.id;

	useEffect(() => {
		fetch(`http://127.0.0.1:8000/get-workflow-files/${id}`, {
			headers: {
				'Content-Type': 'application/json',
				Authorization: `Bearer ${jwtToken}`
			}
		})
			.then((res) => res.json())
			.then((filesJson) => {
				const fileUrlsJson = filesJson['workflow-file-urls'];
				// console.log(filesJson['workflow-file-urls']);
				setAudioUrls(fileUrlsJson['audio']);
				setImageUrls(fileUrlsJson['images']);
				setVideoUrls(fileUrlsJson['videos']);
				setVideoEditingUrls(fileUrlsJson['video_editing']);
			})
			.catch((err) => console.log(err));
	}, []);

	// console.log('hi');
	return (
		<React.Fragment>
			<div className={styles.dashboardMenuContainer}>
				<DashboardMenu />
			</div>
			<div>{audioUrls}</div>
			<div>
				{imageUrls.map((imageUrl) => {
					console.log(imageUrl);
					// return <div key={imageUrl}>{imageUrl}</div>;
					return <img key={imageUrl} src={imageUrl} />;
				})}
			</div>
			<div>{videoUrls}</div>
			<div>{videoEditingUrls}</div>
		</React.Fragment>
	);
};

export async function getStaticProps({ params }) {
	console.log('props');
	return {
		props: {
			id: params.id
		}
	};
}

export async function getStaticPaths() {
	// const workflowIds = localStorage.getItem('workflowIds');
	// const jwt = localStorage.getItem('jwt');
	const jwt =
		'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNTkyMzMyOTI1LCJqdGkiOiIyNzhiMjAyMTQyNTY0MWIxYTc5NjJmNGE4NzI2ZjcwNSIsInVzZXJfaWQiOjQ1fQ.rGAuQaDnBaueRKcE6ZE4t7Ym4Y-HbHqFYn-k-iWr1-0'; // console.log(jwtToken);
	console.log(jwt);
	const workflowIdsResponse = await fetch('http://localhost:8000/get-workflow-ids', {
		headers: {
			'Content-Type': 'application/json',
			Authorization: `Bearer ${jwt}`
		}
	});
	const workflowIdsJson = await workflowIdsResponse.json();
	console.log(workflowIdsJson);
	const workflowIdsArray = await workflowIdsJson['workflow-ids'];
	console.log(workflowIdsArray);
	const paths = workflowIdsArray.map((workflowId) => {
		return {
			params: {
				id: workflowId.toString()
			}
		};
	});
	console.log(paths);
	return {
		paths,
		fallback: false
	};
}

export default Workflow;
