import styles from '../styles/scss/workflow-description.module.scss';
import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

const WorkflowDescription = ({ id }) => {
	const [ description, setDescription ] = useState();
	const jwtToken = useSelector((state) => state.jwtToken);
	const [ fetched, setFetched ] = useState(false);
	// const updateDescription = (e) => {
	// 	setDescription(e.target.value);
	// };
	useEffect(
		() => {
			if (!fetched) {
				async function fetchData() {
					const descRes = await fetch(`http://127.0.0.1:8000/get-workflow-description/${id}`, {
						headers: {
							'Content-Type': 'application/json',
							Authorization: `Bearer ${jwtToken}`
						}
					});
					const descJson = await descRes.json();
					console.log(descJson);
					setDescription(descJson.description);
				}
				fetchData();
				setFetched(true);
			}
			// .then((res) => res.json())
			// .then((res) => {
			// 	console.log(res.description);
			// 	setDescription(res.description);
			// })
			// .catch((err) => console.log(err));
		},
		[ description ]
	);
	return (
		<React.Fragment>
			<h2 className={styles.descriptionTitle}>Description</h2>
			<br />
			<textarea
				name="description"
				className={styles.description}
				defaultValue={description ? description : 'none'}
			/>
		</React.Fragment>
	);
};

export default WorkflowDescription;
