import styles from '../styles/scss/sign-up.module.scss';
import Link from 'next/link';
import classnames from 'classnames';
import { useState } from 'react';

const SignUp = () => {
	const [ email, setEmail ] = useState('');
	const [ password, setPassword ] = useState('');
	const [ username, setUsername ] = useState('');

	const [ emailInputEmpty, setEmailInputEmpty ] = useState(true);
	const [ passwordInputEmpty, setPasswordInputEmpty ] = useState(true);

	const updateEmail = (input) => {
		setEmail(input);
		if (input === '') setEmailInputEmpty(true);
		else setEmailInputEmpty(false);
	};

	const updatePassword = (input) => {
		setPassword(input);
		if (input === '') setPasswordInputEmpty(true);
		else setPasswordInputEmpty(false);
	};

	return (
		<main className={styles.signUpContainer}>
			<section className={styles.textSection}>
				<div className={styles.logoImage}>
					<img src="./images/logo.jpg" alt="Workflow" />
				</div>
				<div className={styles.signUpSection}>
					<h1>
						Sign Up to <br /> Workflow
					</h1>
					<div className={styles.googleSignIn}>
						<img src="/images/google_logo.png" />
						<h4>Sign Up with Google</h4>
					</div>
					<div className={styles.emailSignIn}>
						<p>Or sign-up with email</p>
						<form>
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.emailLabel]: true,
									[styles.passwordLabelHover]: !emailInputEmpty
								})}
							>
								Username
							</h4>
							<input value={email} onChange={(e) => updateEmail(e.target.value)} type="text" required />
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.emailLabel]: true,
									[styles.passwordLabelHover]: !emailInputEmpty
								})}
							>
								Email
							</h4>
							<input value={email} onChange={(e) => updateEmail(e.target.value)} type="email" required />
							<h4
								className={classnames({
									[styles.label]: true,
									[styles.passwordLabel]: true,
									[styles.emailLabelHover]: !passwordInputEmpty
								})}
							>
								Password
							</h4>
							<input
								value={password}
								onChange={(e) => updatePassword(e.target.value)}
								type="password"
								required
							/>
							<div className={styles.submitEmailForm}>Create Your Account</div>
						</form>
						<p className={styles.legal}>
							By signing up, I agree to the <span className={styles.underline}>Privacy Policy</span> and{' '}
							<span className={styles.underline}>Terms of Service</span>
						</p>
					</div>
					<h3 className={styles.switchToLogin}>
						Have an account?{'  '}
						<Link href="/login">
							<span className={styles.loginBtn}>Log in</span>
						</Link>
					</h3>
				</div>
				<div className={styles.copyright}>&copy; 2020. All Rights Reserved. Workflow.</div>
			</section>
			<section className={styles.slideshowSection} />
		</main>
	);
};

export default SignUp;
