import styles from '../styles/scss/workflow-item.module.scss';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useState, useRef } from 'react';

const WorkflowItem = ({ workflow }) => {
	const [ workflowContainerGripped, setWorkflowContainerGripped ] = useState(false);
	// const workflowRef = useRef();

	const capitalize = (txt) => {
		return txt.slice(0, 1).toUpperCase() + txt.slice(1);
	};

	const profileImage = useSelector((state) => state.profileImage);

	// const gripWorkflowContainer = () => {
	// 	setWorkflowContainerGripped(true);
	// };

	// const releaseWorkflowContainer = () => {
	// 	setWorkflowContainerGripped(false);
	// };

	// const moveWorkflow = (e) => {
	// 	if (workflowContainerGripped) {
	// 		workflowRef.current.style.left = e.clientX;
	// 		workflowRef.current.style.top = e.clientY;
	// 	}
	// };

	return (
		<div
			className={styles.workflowContainer}
			// draggable="true"
			// onMouseDown={() => gripWorkflowContainer()}
			// onMouseUp={() => releaseWorkflowContainer()}
			// onMouseMove={(e) => moveWorkflow(e)}
			// ref={workflowRef}
			// style={{ position: workflowContainerGripped ? 'absolute' : 'relative' }}
		>
			<div className={styles.workflowTitle}>{workflow.title}</div>
			<ul className={styles.workflowKeywords}>
				{workflow.keywords.map((keyword) => <li key={keyword}>{capitalize(keyword)}</li>)}
			</ul>
			<div className={styles.workflowUsers}>
				<img src={profileImage} />
			</div>
			<div className={styles.workflowExtraInfo}>
				<h3 className={styles.workflowLastEdited}>1 Week Ago</h3>
				<div className={styles.workflowStatusIcons}>
					<FontAwesomeIcon icon="star" />
					<FontAwesomeIcon icon="check" />
					<FontAwesomeIcon icon="grip-vertical" className={styles.grip} />
				</div>
			</div>
		</div>
	);
};

export default WorkflowItem;
