import { useRef, useState, useEffect } from 'react';
import styles from '../styles/scss/workflow-title.module.scss';
import { useSelector } from 'react-redux';

const WorkflowTitle = ({ oldTitle, id }) => {
	const titleInput = useRef(null);
	const jwtToken = useSelector((state) => state.jwtToken);
	// const [ title, setTitle ] = useState(oldTitle);

	const handleChange = (e) => {
		console.log(titleInput.current.innerText);
	};

	useEffect(() => {
		return () => {
			alert(titleInput.current.innerText);
			console.log(titleInput.current.innerText);
			fetch(`http://localhost:8000/set-workflow-title/${id}`, {
				method: 'PUT',
				headers: {
					'Content-Type': 'application/json',
					Authorization: 'Bearer ' + jwtToken
				},
				body: JSON.stringify({
					title: titleInput.current.innerText
				})
			});
		};
	}, []);

	return (
		<div contentEditable="plaintext-only" ref={titleInput} className={styles.titleInput} onInput={handleChange}>
			{oldTitle ? oldTitle : 'Create title'}
		</div>
	);
};

export default WorkflowTitle;
