import Header from '../components/Header';
import RedBackdrop from '../components/RedBackdrop';
import fs from 'fs';
import path from 'path';
import styles from '../styles/scss/features.module.scss';

const Features = ({ mp4FileNames }) => {
	return (
		<React.Fragment>
			<span>
				<Header />
				<RedBackdrop />
			</span>
			<section className={styles.featuresSection}>
				{mp4FileNames.map((file) => (
					<video className={styles.vid} src={file} type="video/mp4" autoPlay={true} />
				))}
			</section>
		</React.Fragment>
	);
};

export async function getStaticProps() {
	const mp4Path = path.join(process.cwd(), 'public', 'features', 'mp4');
	const mp4FileNames = fs.readdirSync(mp4Path).map((file) => `features/mp4/${file}`);

	return {
		props: {
			mp4FileNames
		}
	};
}

export default Features;
