import { useSelector } from 'react-redux';
// import { jwt } from '../../components/JWT';
import Router from 'next/router';
import React, { useState, useEffect } from 'react';
import DashboardMenu from '../../components/DashboardMenu';
import styles from '../../styles/scss/worklow_editor.module.scss';
import Cookie from 'js-cookie';
import { parseCookie } from '../../components/parseCookie';
import WorkflowDescription from '../../components/WorkflowDescription';

// const dispatch = useDispatch();

const Workflow = (props) => {
	// console.log(Router.pathname);
	const jwtToken = useSelector((state) => state.jwtToken);

	const [ audioUrls, setAudioUrls ] = useState([]);
	const [ imageUrls, setImageUrls ] = useState([]);
	const [ videoUrls, setVideoUrls ] = useState([]);
	const [ videoEditingUrls, setVideoEditingUrls ] = useState([]);
	const [ description, setDescription ] = useState([]);
	const [ title, setTitle ] = useState([]);
	const [ starred, setStarred ] = useState([]);
	const [ finished, setFinished ] = useState([]);
	// const id = Router.query.id;
	const id = props.id;

	useEffect(() => {
		fetch(`http://127.0.0.1:8000/get-workflow-files/${id}`, {
			headers: {
				'Content-Type': 'application/json',
				Authorization: `Bearer ${jwtToken}`
			}
		})
			.then((res) => res.json())
			.then((filesJson) => {
				const fileUrlsJson = filesJson['workflow-file-urls'];
				// console.log(filesJson['workflow-file-urls']);
				setAudioUrls(fileUrlsJson['audio']);
				setImageUrls(fileUrlsJson['images']);
				setVideoUrls(fileUrlsJson['videos']);
				setVideoEditingUrls(fileUrlsJson['video_editing']);
			})
			.catch((err) => console.log(err));

		fetch(`http://127.0.0.1:8000/get-workflow/${id}`, {
			headers: {
				'Content-Type': 'application/json',
				Authorization: `Bearer ${jwtToken}`
			}
		})
			.then((res) => res.json())
			.then((workflowInfoJson) => {
				console.log(workflowInfoJson);
				setDescription(workflowInfoJson.description);
				setTitle(workflowInfoJson.title);
				setStarred(workflowInfoJson.starred);
				setFinished(workflowInfoJson.finished);
			})
			.catch((err) => console.log(err));
	}, []);

	// console.log('hi');
	return (
		<main className={styles.workflowEditorContainer}>
			<div className={styles.dashboardMenuContainer}>
				<DashboardMenu />
			</div>
			<div className={styles.editor}>
				{console.log('description: ' + description)}
				<WorkflowDescription initialDescription={description} />
				{/* Images */}
				<div className={styles.images}>
					<h2>Images</h2>
					{imageUrls.map((imageUrl) => {
						const dotParts = imageUrl.split('.');
						const mimeType = dotParts[dotParts.length - 1];
						return (
							<a href={imageUrl} key={imageUrl} download={`hi.${mimeType}`}>
								<img src={imageUrl} className={styles.workflowImage} />
							</a>
						);
					})}
				</div>
				{/* audio */}
				<div className={styles.audios}>
					<h2>Audio</h2>
					{audioUrls.map((audioUrl) => {
						const dotParts = audioUrl.split('.');
						const mimeType = dotParts[dotParts.length - 1];
						return (
							<audio key={audioUrl} controls>
								<source src={audioUrl} type={`audio/${mimeType}`} />;
							</audio>
						);
					})}
				</div>
				<div className={styles.videos}>
					<h2>Videos</h2>
					{videoUrls.map((videoUrl) => {
						const dotParts = videoUrl.split('.');
						const mimeType = dotParts[dotParts.length - 1];

						// console.log(`video/${mimeType}`);
						if (mimeType !== 'mov') {
							return (
								<video
									key={videoUrl}
									className={styles.workflowVideo}
									width="320"
									height="240"
									controls
								>
									<source src={videoUrl} type={`video/${mimeType}`} />;
								</video>
							);
						} else {
							return <React.Fragment />;
						}
					})}
				</div>
				<div>{videoEditingUrls}</div>
			</div>
		</main>
	);
};

export async function getServerSideProps({ params }) {
	console.log(params.id);
	return {
		props: {
			id: params.id
		}
	};
}

export async function getServerSidePaths({ req }) {
	const cookies = parseCookie(req);
	const res = await fetch('http://127.0.0.1:8000/get-workflow-ids', {
		headers: {
			'Content-Type': 'application/json',
			Authorization: cookies.jwt
		}
	});
	const resJson = res.json();
	const workflowIds = resJson['workflow-ids'];
	const paths = workflowIds.map((workflowId) => {
		return {
			params: {
				id: workflowId.toString()
			}
		};
	});
	return {
		paths,
		fallback: false
	};
}

export default Workflow;
