		// 	body.offsetHeight,
		// 	html.clientHeight,
		// 	html.scrollHeight,
		// 	html.offsetHeight
		// );
		const height = document.querySelector('html').getClientRects()[0].height;
		backdropRef.current.style.height = height;
		console.log(height);
	};
	{
		var height = document.querySelector('html').getClientRects()[0].height;
		backdropRef.current.style.height = height;
		console.log(height);
	}
	return <main ref={backdropRef} onLoad={changeHeight} className={styles.redBackdrop} />;
};

export default RedBackdrop;
