import styles from '../styles/scss/redBackdrop.module.scss';
import { useEffect, useRef } from 'react';

const RedBackdrop = () => {
	const backdropRef = useRef();
	// useEffect(
	// 	() => {
	// 		backdropRef.current.style.height = screen.height;
	// 		console.log(screen.height);
	// 	},
	// 	[ backdropRef ]
	// );
	// const changeHeight = () => {
	// 	var body = document.body;
	// 	var html = document.documentElement;

	// 	// var height = Math.max(
	// 	// 	body.scrollHeight,
	// 	// 	body.offsetHeight,
	// 	// 	html.clientHeight,
	// 	// 	html.scrollHeight,
	// 	// 	html.offsetHeight
	// 	// );
	// 	const height = document.querySelector('html').getClientRects()[0].height;
	// 	backdropRef.current.style.height = height;
	// 	console.log(height);
	// };
	useEffect(
		() => {
			var height = document.querySelector('html').getClientRects()[0].height;
			backdropRef.current.style.height = height;
			console.log(height);
		},
		[ backdropRef ]
	);
	return <main ref={backdropRef} className={styles.redBackdrop} />;
};

export default RedBackdrop;
