import styles from '../styles/scss/workflow-modal.module.scss';
import classnames from 'classnames';
import { useEffect, useState } from 'react';

const CreateWorkflowModal = ({ displayModal, setDisplayModal }) => {
	const [ newWorkflowTitle, setNewWorkflowTitle ] = useState(null);
	const toggleModalVisibility = () => {
		setDisplayModal(!displayModal);
	};
	return (
		<React.Fragment>
			<div
				className={classnames({
					[styles.addWorkflowModal]: true,
					[styles.modalDeactivate]: !displayModal
				})}
			>
				<form>
					{/* <img src="images/menu-icons/workflow_images" */}
					<input value={newWorkflowTitle} type="text" />
					{/* <input value={title} type="text" /> */}
				</form>
			</div>
			<div
				className={classnames({
					[styles.addWorkflowModalBackground]: true,
					[styles.modalDeactivate]: !displayModal
				})}
				onClick={() => toggleModalVisibility()}
			/>
		</React.Fragment>
	);
};

export default CreateWorkflowModal;
