import { useRef, useState, useEffect } from 'react';
import styles from '../styles/scss/workflow-title.module.scss';
import { useSelector } from 'react-redux';

const WorkflowTitle = ({ oldTitle, id }) => {
	const titleInput = useRef(null);
	const jwtToken = useSelector((state) => state.jwtToken);
	const inputLengthMultiplier = 18;

	const pushTitleChangeToBackend = async () => {
		await fetch(`http://localhost:8000/set-workflow-title/${id}`, {
			method: 'PUT',
			headers: {
				'Content-Type': 'application/json',
				Authorization: 'Bearer ' + jwtToken
			},
			body: JSON.stringify({
				title: titleInput.current.innerText
			})
		});
	};

	useEffect(() => {
		// titleInput.current.style.width =
		// 	((titleInput.current.value.length + 1) * inputLengthMultiplier).toString() + 'px';
	}, []);

	const handleChange = async () => {
		if (titleInput.current.innertText !== oldTitle) {
			console.log('changing');
			await pushTitleChangeToBackend();
		}
	};

	const changeWidth = () => {
		titleInput.current.style.width = (titleInput.current.value.length * inputLengthMultiplier).toString() + 'px';
		console.log(((titleInput.current.value.length + 1) * 8).toString() + 'px');
	};

	return (
		// <input
		// 	ref={titleInput}
		// 	className={styles.titleInput}
		// 	onBlur={handleChange}
		// 	tabIndex={0}
		// defaultValue={oldTitle ? oldTitle : 'Create title'}
		// defaultValue={oldTitle}
		// onChange={changeWidth}
		// onLoad={changeWidth}
		// />
		<h2
			contentEditable={'plaintext-only'}
			defaultValue={oldTitle}
			ref={titleInput}
			className={styles.titleInput}
			onBlur={handleChange}
		/>
	);
};

export default WorkflowTitle;
