import styles from '../../styles/scss/statistics.module.scss';
import classnames from 'classnames';
import { useSelector, useDispatch } from 'react-redux';
import { setProfileImage } from '../../components/actions/profileImageActions';
import { setProfileName } from '../../components/actions/profileNameActions';
import DashboardMenu from '../../components/DashboardMenu';
// import Chart from 'chart.js';
import { Bar, Line, Pie, Doughnut } from 'react-chartjs-2';
import { useRef, useState, useEffect } from 'react';

const Dashboard = () => {
	const jwt = useSelector((state) => state.jwtToken);
	const statsCanvas = useRef();
	const [ userFilesBreakdown, setUserFilesBreakdown ] = useState();
	const [ userFilesLabels, setUserFilesLabels ] = useState();

	const capitalize = (str) => {
		return str.slice(0, 1).toUpperCase() + str.slice(1);
	};

	const fetchFileBreakdown = async () => {
		const res = await fetch('http://localhost:8000/workflows-file-breakdown', {
			headers: {
				'Content-Type': 'application/json',
				Authorization: 'Bearer ' + jwt
			}
		});
		if (res.status === 200) {
			const resJson = await res.json();
			const info = resJson['msg'];

			setUserFilesLabels(Object.keys(info).map((label) => capitalize(label)));
			setUserFilesBreakdown(Object.values(info));
		}
	};

	useEffect(() => {
		fetchFileBreakdown();
	}, []);

	const doughtnutChart = (canvas) => {
		// const ctx = canvas.getContext('2d');
		return {
			labels: userFilesLabels,
			datasets: [
				{
					// label: 'File Breakdown',
					data: userFilesBreakdown,
					backgroundColor: [ '#DC1C13', '#EA4C46', '#F07470', '#F1959B' ]
				}
			]
		};
	};

	// const loadCharts = () => {};

	// useEffect(
	// 	() => {
	// 		loadCharts();
	// 		return () => {
	// 			cleanup;
	// 		};
	// 	},
	// 	[ input ]
	// );

	return (
		<main className={styles.statisticsContainer}>
			<div className={styles.dashboardMenuContainer}>
				<DashboardMenu />
			</div>
			<div className={styles.statisticsSection}>
				<Doughnut data={doughtnutChart} responsive={true} options={{ height: 100, width: 100 }} />
			</div>
		</main>
	);
};

export default Dashboard;
