import { useRef, useState, useEffect } from 'react';
import styles from '../styles/scss/workflow-title.module.scss';

const WorkflowTitle = ({ oldTitle }) => {
	const titleInput = useRef(null);
	// const [ title, setTitle ] = useState(oldTitle);

	const handleChange = (e) => {
		console.log(titleInput.current.value);
	};

	useEffect(() => {
		return () => {
			console.log(titleInput.current.value);
		};
	}, []);

	return (
		<div contenteditable="plaintext-only" ref={titleInput} className={styles.titleInput} onChange={handleChange}>
			{oldTitle ? oldTitle : 'Create title'}
		</div>
	);
};

export default WorkflowTitle;
