import styles from '../../styles/scss/statistics.module.scss';
import classnames from 'classnames';
import { useSelector, useDispatch } from 'react-redux';
import { setProfileImage } from '../../components/actions/profileImageActions';
import { setProfileName } from '../../components/actions/profileNameActions';
import DashboardMenu from '../../components/DashboardMenu';
// import Chart from 'chart.js';
import { Bar, Line, Pie } from 'react-chartjs-2';
import { useRef } from 'react';

const Dashboard = () => {
	const statsCanvas = useRef();

	const doughtnutChart = (canvas) => {
		// const ctx = canvas.getContext('2d');
		return {
			labels: [ 'One', 'Two', 'Three' ],
			datasets: [
				{
					label: 'Test',
					data: [ 1, 2, 3 ],
					backgroundColor: [ '#DC1C13', '#EA4C46', '#F07470' ]
				}
			]
		};
	};

	// const loadCharts = () => {};

	// useEffect(
	// 	() => {
	// 		loadCharts();
	// 		return () => {
	// 			cleanup;
	// 		};
	// 	},
	// 	[ input ]
	// );

	return (
		<main className={styles.statisticsContainer}>
			<div className={styles.dashboardMenuContainer}>
				<DashboardMenu />
			</div>
			<div className={styles.statisticsSection}>
				<Pie data={doughtnutChart} responsive={true} options={{ height: 100, width: 100 }} />
			</div>
		</main>
	);
};

export default Dashboard;
