import styles from "../styles/scss/workflow-item.module.scss"
import {useSelector} from "react-redux"

const WorkflowItem = ({workflow}) => {

    const capitalize = (txt) => {
		return txt.slice(0,1).toUpperCase() + txt.slice(1)
	}

    const profileImage = useSelector(state=>state.profileImage)

    return (
        <div className={styles.workflowContainer}>
            <div className={styles.workflowTitle}>{workflow.title}</div>
            <ul className={styles.workflowKeywords}>
                {workflow.keywords.map(keyword=>(
                    <li key={keyword}>
                        {capitalize(keyword)}
                    </li>
                ))}
            </ul>
            <div className={styles.workflowUsers}>
                <img src={profileImage} />
            </div>
        </div>
    )
}

export default WorkflowItem;
