import "../styles/global.css";
import {createStore, combineReducers} from "redux";
import {Provider} from "react-redux"

const increment = () => {
  return {
    type: "INCREMENT"
  }
}

const decrement = () => {
  return {
    type: "DECREMENT"
  }
}

const signIn = () => {
  return {
    type:"SIGN_IN"
  }
}

const signUp = () => {
  return {
    type:"SIGN_UP"
  }
}

// const countReducer = (state=0,action) => {
//   switch (action.type) {
//     case "INCREMENT":
//       return state + 1;
//     case "DECREMENT":
//       return state - 1;
//     default:
//       return state;
//   }
// }

const loginReducer = (state=false,action) => {
  switch (action.type) {
    case "SIGN_IN":
      return true;
    case "SIGN_OUT":
      return false;
    default:
      state;
  }
}

// const loggedReducer = (state = false,action) => {
//   switch (action.type) {
//     case "SIGN_IN":
//       return true;
//     case "SIGN_OUT":
//       return false;
//     default:
//       state;
//   }
// }

const allReducers = combineReducers({
  // count: countReducer,
  login: loginReducer,
});

const myStore = createStore(allReducers);

const App = ({ Component, pageProps }) => {
  return (
  <Provider store={myStore}>
  <Component {...pageProps} />
  </Provider>
  );
};

export default App;
